import React, { useState, useEffect } from "react";

export default function Students() {
  const [students, setStudents] = useState([]);
  const [form, setForm] = useState({
    name: "",
    email: "",
    studentId: "",
    pincode: "",
    district: "",
    state: "",
    country: "",
  });
  const [error, setError] = useState("");

  // Fetch all students
  const fetchStudents = async () => {
    try {
      const res = await fetch("http://127.0.0.1:8000/api/students/");
      const data = await res.json();
      setStudents(data);
    } catch (error) {
      console.error("Error fetching students:", error);
    }
  };

  useEffect(() => {
    fetchStudents();
  }, []);

  // Handle form input
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // Fetch location details from pincode
  const handlePincodeLookup = async () => {
    if (!form.pincode) return;

    try {
      const res = await fetch(
        `https://api.postalpincode.in/pincode/${form.pincode}`
      );
      const data = await res.json();

      if (data[0].Status === "Success") {
        const postOffice = data[0].PostOffice[0];
        setForm({
          ...form,
          district: postOffice.District || "",
          state: postOffice.State || "",
          country: postOffice.Country || "",
        });
        setError("");
      } else {
        setError("Invalid Pincode. Please enter a valid one.");
        setForm({ ...form, district: "", state: "", country: "" });
      }
    } catch (err) {
      console.error("Error fetching pincode data:", err);
      setError("Error fetching location details. Try again.");
    }
  };

  // Add new student
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const res = await fetch("http://127.0.0.1:8000/api/students/", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(form),
      });

      if (res.ok) {
        setForm({
          name: "",
          email: "",
          studentId: "",
          pincode: "",
          district: "",
          state: "",
          country: "",
        });
        fetchStudents();
      } else {
        const errorData = await res.json();
        alert("Error adding student: " + JSON.stringify(errorData));
      }
    } catch (error) {
      console.error("Error adding student:", error);
    }
  };

  return (
    <div className="p-6 bg-white shadow-lg rounded-lg">
      <h1 className="text-2xl font-bold text-red-700 mb-4">
        Student Management
      </h1>

      {/* Add Student Form */}
      <form onSubmit={handleSubmit} className="grid grid-cols-2 gap-4 mb-6">
        <input
          type="text"
          name="name"
          placeholder="Name"
          value={form.name}
          onChange={handleChange}
          className="border p-2 rounded"
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={form.email}
          onChange={handleChange}
          className="border p-2 rounded"
          required
        />
        <input
          type="text"
          name="studentId"
          placeholder="Student ID"
          value={form.studentId}
          onChange={handleChange}
          className="border p-2 rounded"
          required
        />

        {/* Pincode Field */}
        <div className="flex gap-2">
          <input
            type="text"
            name="pincode"
            placeholder="Pincode"
            value={form.pincode}
            onChange={handleChange}
            onBlur={handlePincodeLookup}
            className="border p-2 rounded flex-1"
          />
          <button
            type="button"
            onClick={handlePincodeLookup}
            className="bg-gray-200 px-3 rounded hover:bg-gray-300"
          >
            Autofill
          </button>
        </div>

        {error && <p className="text-red-600 col-span-2">{error}</p>}

        <input
          type="text"
          name="district"
          placeholder="District"
          value={form.district}
          onChange={handleChange}
          className="border p-2 rounded"
        />
        <input
          type="text"
          name="state"
          placeholder="State"
          value={form.state}
          onChange={handleChange}
          className="border p-2 rounded"
        />
        <input
          type="text"
          name="country"
          placeholder="Country"
          value={form.country}
          onChange={handleChange}
          className="border p-2 rounded"
        />

        <button
          type="submit"
          className="col-span-2 bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700"
        >
          Add Student
        </button>
      </form>

      {/* Students Table */}
      <h2 className="text-xl font-semibold text-gray-800 mb-2">All Students</h2>
      <table className="w-full border-collapse border border-gray-300">
        <thead>
          <tr className="bg-red-100 text-left">
            <th className="border border-gray-300 p-2">Name</th>
            <th className="border border-gray-300 p-2">Email</th>
            <th className="border border-gray-300 p-2">Student ID</th>
            <th className="border border-gray-300 p-2">District</th>
            <th className="border border-gray-300 p-2">State</th>
            <th className="border border-gray-300 p-2">Country</th>
          </tr>
        </thead>
        <tbody>
          {students.map((s) => (
            <tr key={s.id} className="hover:bg-gray-100">
              <td className="border border-gray-300 p-2">{s.name}</td>
              <td className="border border-gray-300 p-2">{s.email}</td>
              <td className="border border-gray-300 p-2">{s.studentId}</td>
              <td className="border border-gray-300 p-2">{s.district}</td>
              <td className="border border-gray-300 p-2">{s.state}</td>
              <td className="border border-gray-300 p-2">{s.country}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
